mapper.select:
WITH first_open_day AS (SELECT nvl(MAX(bc.gas_day) + 1, to_date('2000-01-01', 'YYYY-MM-DD')) AS first_day FROM tpa_tbalance_closing bc WHERE bc.idn_balance_closing_type = (SELECT bct.idn_balance_closing_type FROM tpa_tbalance_closing_type bct WHERE bct.type_code = 'DEFINITIVE') AND bc.idn_pipeline_system = #{idn_active_system} AND bc.version_date = (SELECT MAX(bcx.version_date) FROM tpa_tbalance_closing bcx WHERE bcx.gas_day = bc.gas_day AND bcx.idn_pipeline_system = #{idn_active_system} AND bcx.idn_balance_closing_type = (SELECT t.idn_allocation_type FROM tpa_tallocation_type t WHERE t.type_code = 'COMMERCIAL'))),
adjustments AS (SELECT aa.gas_day, c.idn_user_group AS idn_shipper, aa.idn_zone, aa.idn_contract, aa.operator_comments, aa.adjust_value, aa.status FROM tpa_tallocation_adjust aa, tpa_tcontract c, tpa_tnomination_concept nc WHERE aa.version_date = (SELECT MAX(aaxx.version_date) FROM tpa_tallocation_adjust aaxx WHERE aaxx.adjust_code = aa.adjust_code AND aaxx.idn_contract = aa.idn_contract AND aaxx.idn_nomination_concept = nc.idn_nomination_concept) AND nc.idn_nomination_concept = aa.idn_nomination_concept AND nc.concept_code = 'Alloc_acc_month_adjust' AND aa.idn_contract = c.idn_contract AND EXISTS (SELECT 1 FROM tpa_tcontract_consolidate cc WHERE cc.idn_contract = c.idn_contract) AND c.idn_pipeline_system = #{idn_active_system} AND c.idn_user_group = #{shipperId}
AND c.idn_user_group = #{shipperId}
AND aa.gas_day >= #{startDate} AND c.end_date >= #{startDate} and #{endDate} >= aa.gas_day and #{endDate} >= c.start_date ),
allocation AS (SELECT a.gas_day, c.idn_user_group AS idn_shipper, ad.idn_zone, ad.idn_contract, decode(nc.concept_code, 'Accumulated_Imbalance_Stock', ad.allocated_value) AS allocated_value, decode(nc.concept_code, 'Alloc_acc_month_adjust', ad.allocated_value) AS adjusted_value FROM tpa_tallocation a, tpa_tallocation_data ad, tpa_tcontract c, tpa_tnomination_concept nc WHERE a.idn_allocation = ad.idn_allocation AND a.idn_pipeline_system = c.idn_pipeline_system AND a.idn_allocation_type = (SELECT t.idn_allocation_type FROM tpa_tallocation_type t WHERE t.type_code = 'COMMERCIAL') AND a.version_date = (SELECT MAX(ax.version_date) FROM tpa_tallocation ax, tpa_tallocation_origin ao WHERE ax.gas_day = a.gas_day AND ax.idn_pipeline_system = a.idn_pipeline_system AND ax.idn_allocation_type = a.idn_allocation_type AND ax.idn_allocation_origin = ao.idn_allocation_origin AND ao.origin_code IN ('BALANCE', 'CLOSING_PROCESS')) AND ad.idn_nomination_concept = nc.idn_nomination_concept AND nc.concept_code IN ('Accumulated_Imbalance_Stock', 'Alloc_acc_month_adjust') AND ad.idn_contract = c.idn_contract AND EXISTS (SELECT 1 FROM tpa_tcontract_consolidate cc WHERE cc.idn_contract = c.idn_contract) AND a.idn_pipeline_system = #{idn_active_system} AND c.idn_pipeline_system = #{idn_active_system}
AND c.idn_user_group = #{shipperId}
AND c.idn_user_group = #{shipperId} AND a.gas_day >= #{startDate} AND c.end_date >= #{startDate} and #{endDate} >= a.gas_day and #{endDate} >= c.start_date )
SELECT al.gas_day as gasDay, al.idn_shipper as idnShipper, (SELECT ug.user_group_id FROM tpa_tuser_group ug WHERE ug.idn_user_group = al.idn_shipper) AS shipperCode, (SELECT ug.short_name FROM tpa_tuser_group ug WHERE ug.idn_user_group = al.idn_shipper) AS shortName, al.idn_zone as idnZone, (SELECT z.zone_code FROM tpa_tzone z WHERE z.idn_zone = al.idn_zone) AS zoneCode, SUM(al.allocated_value) AS accImbalanceMonth, SUM(al.adjusted_value) AS allocatedAdjust, MAX(decode(ad.status, 'ACCEPTED', ad.adjust_value, NULL)) AS pendingAdjust, SUM(al.allocated_value) + nvl(MAX(decode(ad.status, 'ACCEPTED', ad.adjust_value, NULL)), 0) AS finalAccImbalanceStock, ad.operator_comments, CASE WHEN al.gas_day >= (SELECT first_day FROM first_open_day) THEN 'Y' ELSE 'N' END AS isEditable FROM allocation al, adjustments ad WHERE ad.gas_day(+) = al.gas_day AND ad.idn_zone(+) = al.idn_zone
and al.idn_zone = #{zoneId}
AND ad.idn_contract(+) = al.idn_contract AND ad.idn_shipper(+) = al.idn_shipper GROUP BY al.gas_day, al.idn_shipper, al.idn_zone, ad.operator_comments ORDER BY 1, 3, 6
