mapper.select:
	select tcon.idn_contract as key, tcon.contract_code as value
	  from tpa_tcontract tcon
     where EXISTS (SELECT 1
             FROM tpa_tcontract_consolidate tconso
            WHERE tconso.idn_contract = tcon.idn_contract)
            and tcon.idn_pipeline_system = #{systemId}

			and #{gasDay} >= tcon.start_date and tcon.end_date >= #{gasDay}


	       	and tcon.idn_user_group = #{shipperId}

     order by upper(tcon.contract_code) asc

SELECT tdata.idn_allocation_data, tal.gas_day, tgr.idn_user_group, tgr.user_group_id, tgr.short_name, tcon.idn_contract, tcon.contract_code, tnomp.idn_system_point, tnomp.point_code, (select x.type_code from TPA_TSYSTEM_POINT_TYPE x where x.idn_system_point_type= tnomp.idn_system_point_type) point_type_code, tdata.contracted_value * #{factorFromDefaultUnit} as contracted_value, tdata.nominated_value * #{factorFromDefaultUnit} as nominated_value, tdata.allocated_value * #{factorFromDefaultUnit} as allocated_value, tao.origin_desc, tal.version_date FROM tpa_tallocation tal, tpa_tallocation_data tdata, tpa_tcontract tcon, tpa_tuser_group tgr, tpa_tsystem_point tnomp, tpa_tallocation_origin tao WHERE tdata.idn_allocation = tal.idn_allocation AND tal.idn_allocation_type = (SELECT talty.idn_allocation_type FROM tpa_tallocation_type talty WHERE talty.type_code = 'COMMERCIAL') AND tdata.idn_nomination_concept = (SELECT tncon.idn_nomination_concept FROM tpa_tnomination_concept tncon WHERE tncon.concept_code = 'ENERGY')
and tal.gas_day = #{gasDay}
and tdata.idn_contract = #{contractId}
#{item}
and tdata.idn_nomination_point in (-1)
AND tcon.idn_contract = tdata.idn_contract
and tcon.idn_user_group = #{shipperId}
AND tal.version_date = (SELECT MAX(ax.version_date) FROM tpa_tallocation ax, tpa_tallocation_origin ao WHERE ax.gas_day = tal.gas_day AND ax.idn_pipeline_system = tal.idn_pipeline_system AND ax.idn_allocation_type = tal.idn_allocation_type AND ax.idn_allocation_origin = ao.idn_allocation_origin AND ao.origin_code != 'BALANCE')
and cast(tal.version_date as date) >= #{generatedFrom}
and #{generatedTo} >= cast(tal.version_date as date)
AND tgr.idn_user_group = tcon.idn_user_group AND tnomp.idn_system_point = tdata.idn_nomination_point AND tao.idn_allocation_origin = tal.idn_allocation_origin AND tao.origin_code != 'BALANCE' order by upper(tgr.user_group_id) asc, upper(tcon.contract_code) asc, upper(tnomp.point_code) asc, tal.version_date
