mapper.select:
	select tcon.idn_contract as key, tcon.contract_code as value
	  from tpa_tcontract tcon
     where EXISTS (SELECT 1
             FROM tpa_tcontract_consolidate tconso
            WHERE tconso.idn_contract = tcon.idn_contract)
            and tcon.idn_pipeline_system = #{systemId}

			and #{gasDay} >= tcon.start_date and tcon.end_date >= #{gasDay}


	       	and tcon.idn_user_group = #{shipperId}

     order by upper(tcon.contract_code) asc

WITH allocation_review AS (SELECT ar.* FROM tpa_tallocation_review ar, tpa_tcontract tcon WHERE tcon.idn_contract = ar.idn_contract AND tcon.idn_pipeline_system = #{systemId}
and ar.gas_day = #{gasDay}
and tcon.idn_user_group = #{shipperId}
and ar.idn_contract = #{contractId}
#{item}
and ar.idn_system_point in (-1)
and ar.version_date = (SELECT MAX(arx.version_date) FROM tpa_tallocation_review arx WHERE arx.gas_day = ar.gas_day and arx.idn_contract = ar.idn_contract and nvl(arx.idn_system_point, -1) = nvl(ar.idn_system_point, -1) and arx.idn_zone = ar.idn_zone and arx.idn_nomination_concept = ar.idn_nomination_concept))
select * from (SELECT tdata.idn_allocation_data, tal.gas_day, tgr.idn_user_group, tgr.short_name, tgr.user_group_id, tcon.idn_contract, tcon.contract_code, tnomp.idn_system_point, tnomp.point_code, (select x.type_code from TPA_TSYSTEM_POINT_TYPE x where x.idn_system_point_type= tnomp.idn_system_point_type) point_type_code, tdata.idn_zone, tzone.zone_code, tarea.idn_area, tarea.area_code, tdata.idn_nomination_concept, tdata.contracted_value * #{factorFromDefaultUnit} as contracted_value, tdata.nominated_value * #{factorFromDefaultUnit} as nominated_value, tdata.allocated_value * #{factorFromDefaultUnit} as allocated_value, case when trev.status != 'ALLOCATED' and tal.version_date > nvl(trev.version_date, tal.version_date) then 'Y' else 'N' end as allocation_changed, trev.allocation_value * #{factorFromDefaultUnit} as allocation_value, trev.reviewed_value * #{factorFromDefaultUnit} as shipper_value, trev.shipper_comments, decode(trev.reviewed_value, NULL, NULL, (tdata.allocated_value - trev.reviewed_value) * #{factorFromDefaultUnit}) AS allocation_diff, decode(trev.reviewed_value, NULL, NULL, decode(tdata.allocated_value, 0, decode(trev.reviewed_value, 0, 0, 1), (tdata.allocated_value - trev.reviewed_value) / tdata.allocated_value)) AS allocation_perc, trev.status, trev.idn_allocation_review, trev.review_code FROM tpa_tallocation tal, tpa_tallocation_data tdata, allocation_review trev, tpa_tcontract tcon, tpa_tuser_group tgr, tpa_tsystem_point tnomp, tpa_tzone tzone, tpa_tarea tarea WHERE tdata.idn_allocation = tal.idn_allocation AND tal.idn_allocation_type = (SELECT talty.idn_allocation_type FROM tpa_tallocation_type talty WHERE talty.type_code = 'COMMERCIAL') AND tal.idn_pipeline_system = #{systemId} AND tal.version_date = (SELECT MAX(ax.version_date) FROM tpa_tallocation ax WHERE ax.gas_day = tal.gas_day AND ax.idn_pipeline_system = tal.idn_pipeline_system AND ax.idn_allocation_type = tal.idn_allocation_type) AND tdata.idn_nomination_concept = (SELECT tncon.idn_nomination_concept FROM tpa_tnomination_concept tncon WHERE tncon.concept_code = 'ENERGY')
and tal.gas_day = #{gasDay}
and tdata.idn_contract = #{contractId}
#{item}
and tdata.idn_nomination_point in (-1)
AND tcon.idn_contract = tdata.idn_contract
and tcon.idn_user_group = #{shipperId}
AND tgr.idn_user_group = tcon.idn_user_group AND tnomp.idn_system_point = tdata.idn_nomination_point and tzone.idn_zone = tdata.idn_zone and tarea.idn_area = tnomp.idn_area AND trev.gas_day(+) = tal.gas_day AND trev.idn_contract(+) = tdata.idn_contract AND trev.idn_system_point(+) = tdata.idn_nomination_point AND trev.idn_zone (+)= tdata.idn_zone AND trev.idn_nomination_concept (+)= tdata.idn_nomination_concept) t1
and (
#{item}
or t1.status is null
)
and t1.status in ('no_valid_status')
and t1.review_code like #{reviewCode}
order by t1.gas_day asc, upper(t1.point_code) asc, upper(t1.user_group_id) asc, upper(t1.contract_code) asc

SELECT ar.idn_allocation_review, ar.gas_day, ar.idn_contract, ar.idn_system_point, ar.idn_zone, ar.idn_nomination_concept, ar.reviewed_value * #{conversionFactor} as shipper_value, ar.shipper_comments, ar.status, ar.review_code FROM tpa_tallocation_review ar
and ar.gas_day = #{gasDay}
and ar.idn_contract = #{contractId}
and ar.idn_system_point = #{nomPointId}
and ar.idn_zone = #{zoneId}
and ar.idn_nomination_concept = #{nominationConceptId}
and ar.version_date = (SELECT MAX(arx.version_date) FROM tpa_tallocation_review arx WHERE arx.gas_day = ar.gas_day and arx.idn_contract = ar.idn_contract and nvl(arx.idn_system_point, -1) = nvl(ar.idn_system_point, -1) and arx.idn_zone = ar.idn_zone and arx.idn_nomination_concept = ar.idn_nomination_concept)

select tgr.idn_user_group 
from tpa_tuser_group tgr
where tgr.user_group_id=#{groupCode}
