mapper.select:
SELECT ie.gas_day gas_day, ug.user_group_id shipper_id, ug.short_name short_name, c.contract_code contrac_code, nc.concept_desc concept, ie.allocated_flow allocated_value, z.zone_code zone_code, z.idn_zone idn_zone FROM tpa_tinstructed_expost ie, tpa_tcontract c, tpa_tuser_group ug, tpa_tnomination_concept nc, tpa_tuser_group_type b, tpa_tzone z WHERE ie.idn_contract = c.idn_contract AND ie.idn_nomination_concept = nc.idn_nomination_concept AND ie.version_date = (SELECT MAX(iex.version_date) FROM tpa_tinstructed_expost iex WHERE iex.gas_day = ie.gas_day AND iex.idn_contract = ie.idn_contract AND iex.idn_zone = ie.idn_zone) AND c.idn_user_group = ug.idn_user_group AND EXISTS (SELECT 1 FROM tpa_tcontract_consolidate cc WHERE cc.idn_contract = c.idn_contract) AND ug.idn_user_group_type = b.idn_user_group_type AND b.type_code = 'SHIPPER' and z.idn_zone = ie.idn_zone
and c.idn_pipeline_system= #{idn_system}
and ug.idn_user_group = #{idn_user_group}
and c.idn_contract = #{idn_contract}
and z.idn_zone = #{idn_zone}
and ie.gas_day >= #{startDate}
and #{endDate} >=ie.gas_day
order by UPPER(gas_day), UPPER(shipper_id), UPPER(contrac_code)

        select  c.contract_code 
		from tpa_tcontract c
		where c.idn_contract = #{idn_contract}
