mapper.select:
SELECT gf.idn_intraday_gas_flow, gf.idn_user_group, a.version_date, ug.user_group_id, z.zone_code, gf.imbalance_value, gf.margin_value, gf.flow_type, gf.flow_energy, gf.flow_volume, gf.resolve_time, gf.flow_volume/gf.resolve_time AS FLOWTIME, gf.idn_base_inv, bi.hv_value AS hv, gf.comment_operator, gf.comment_shipper, gf.idn_intraday_gas_flow_file, (select file_name from tpa_tintraday_gas_flow_file t where t.idn_intraday_gas_flow_file = gf.idn_intraday_gas_flow_file) as file_name, gf.published, 'N' AS is_total FROM tpa_tintraday_gas_flow gf, tpa_tallocation a, tpa_tallocation_origin ao, tpa_tuser_group ug , tpa_vzone_tpa z, tpa_tbase_inv bi WHERE gf.idn_allocation = a.idn_allocation AND gf.idn_user_group = ug.idn_user_group AND a.idn_allocation_type = (SELECT t.idn_allocation_type FROM tpa_tallocation_type t WHERE t.type_code = 'INTRADAY') AND a.idn_allocation_origin = ao.idn_allocation_origin AND ao.origin_code IN ('BALANCE', 'CLOSING_PROCESS') AND gf.idn_zone = z.idn_zone AND gf.idn_base_inv = bi.idn_base_inv
and gf.idn_zone = #{zoneId}
and a.gas_day = #{date}
and gf.idn_user_group = #{shipperId} and gf.published = 'Y'
AND gf.idn_user_group = #{shipperId}
AND a.idn_allocation=#{timestampVar}
AND a.version_date = (SELECT MAX(ax.version_date) FROM tpa_tallocation ax, tpa_tallocation_origin ao WHERE ax.gas_day = a.gas_day AND ax.idn_pipeline_system = a.idn_pipeline_system AND ax.idn_allocation_type = a.idn_allocation_type AND ax.idn_allocation_origin = ao.idn_allocation_origin AND ao.origin_code IN ('BALANCE', 'CLOSING_PROCESS'))

mapper.update:
		update TPA_TINTRADAY_GAS_FLOW set
             comment_operator =#{operatorComments},
             comment_shipper =#{shipperComments},
             idn_intraday_gas_flow_file = #{idn_intraday_gas_flow_file},
             aud_last_user = #{username}
		where idn_intraday_gas_flow=#{idn_intraday_gas_flow}

		update tpa_tintraday_gas_flow_file set
             binary_data = #{binary_data},
             file_name =#{file_name},
             content_type = #{content_type}
		where idn_intraday_gas_flow_file = #{idn_intraday_gas_flow_file}

		update tpa_tintraday_gas_flow set
             published = #{published}
		where idn_intraday_gas_flow = #{idn_intraday_gas_flow}
