mapper.select:
WITH gas_day AS (SELECT #{gasDay} AS gas_day FROM dual), all_concepts AS (SELECT nc.idn_nomination_concept AS idn, nc.concept_code AS concept_code FROM tpa_tnomination_concept nc WHERE nc.concept_code IN ('Accumulated_Total_Inventory', 'Base_Inventory', 'Acc_Imbalance_Inventory_Operator', 'Acc_Imbalance_Others', 'Acc_Imbalance_Inventory_Others', 'Acc_Imbalance_Inventory_PTTShipper') ), intraday_balance AS (SELECT t.idn_allocation, t.gas_day, t.version_date FROM tpa_tallocation t, tpa_tallocation_origin ao WHERE t.gas_day = (SELECT gas_day FROM gas_day) AND t.idn_pipeline_system = #{systemId} AND ao.origin_code IN ('BALANCE', 'CLOSING_PROCESS') AND t.idn_allocation_origin = ao.idn_allocation_origin AND t.idn_allocation_type = (SELECT idn_allocation_type FROM TPA_TALLOCATION_TYPE WHERE type_code = 'INTRADAY') AND t.gas_day = trunc(t.version_date)
AND t.idn_allocation= #{timestampVar}
AND t.version_date = (SELECT MAX(ax.version_date) FROM tpa_tallocation ax, tpa_tallocation_origin ao WHERE ax.gas_day = t.gas_day and trunc(ax.version_date) = ax.gas_day AND ax.idn_pipeline_system = t.idn_pipeline_system AND ax.idn_allocation_type = t.idn_allocation_type AND ax.gas_day = trunc(ax.version_date) AND ax.idn_allocation_origin = ao.idn_allocation_origin AND ao.origin_code IN ('BALANCE', 'CLOSING_PROCESS'))
), data_global AS (SELECT ad.idn_allocation_data_global, ad.idn_allocation, ad.idn_zone, c.concept_code, z.zone_code || '#' || c.concept_code AS aggregation, ad.idn_nomination_concept, ad.allocated_value, ib.version_date, ib.gas_day FROM intraday_balance ib, tpa_tallocation_data_global ad, all_concepts c, tpa_vzone_tpa z WHERE ad.idn_allocation = ib.idn_allocation AND ad.idn_zone = z.idn_zone AND ad.idn_nomination_concept = c.idn ), base_intraday_setup AS (SELECT ib.idn_allocation, bi.idn_base_inv, bi.gas_day, bi.idn_zone, bi.idn_base_inv_mode, mo.mode_code, z.zone_code, bi.version_date, bi.hv_value, bi.base_inv_value, bi.high_threshold_top, bi.high_threshold_mid, bi.high_threshold, bi.low_threshold, bi.low_threshold_mid, bi.low_threshold_top FROM tpa_tbase_inv bi, tpa_tbase_inv_setup bis, tpa_tbase_inv_mode mo, tpa_tzone z, intraday_balance ib WHERE bi.gas_day BETWEEN bis.start_date AND COALESCE(bis.end_date, (SELECT MIN(bisx.start_date) - 1 FROM tpa_tbase_inv_setup bisx WHERE bisx.idn_zone = bis.idn_zone AND NVL(bisx.end_date, bisx.start_date) >= bisx.start_date AND bisx.start_date > bis.start_date AND bisx.version_date = (SELECT MAX(bisxy.version_date) FROM tpa_tbase_inv_setup bisxy WHERE bisxy.idn_zone = bisx.idn_zone AND bisxy.start_date = bisx.start_date AND bisxy.version_date <= ib.version_date)), to_date('9999-12-31', 'YYYY-MM-DD')) AND bis.version_date = (SELECT MAX(bisy.version_date) FROM tpa_tbase_inv_setup bisy WHERE bisy.idn_zone = bis.idn_zone AND bisy.start_date = bis.start_date AND bisy.version_date <= ib.version_date) AND bi.idn_zone = bis.idn_zone AND bi.idn_base_inv_mode = bis.idn_base_inv_mode AND bi.version_date = (SELECT MAX(biy.version_date) FROM tpa_tbase_inv biy WHERE biy.gas_day = bi.gas_day AND biy.idn_zone = bi.idn_zone AND biy.idn_base_inv_mode = bi.idn_base_inv_mode AND biy.version_date <= ib.version_date) AND bi.idn_base_inv_mode = mo.idn_base_inv_mode AND z.idn_zone = bi.idn_zone ) SELECT * FROM (SELECT dgl.version_date, dgl.allocated_value, dgl.aggregation, CASE WHEN dgl.allocated_value > b.HIGH_THRESHOLD_TOP AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'red' WHEN dgl.allocated_value > b.HIGH_THRESHOLD_MID AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'orange' WHEN dgl.allocated_value > b.HIGH_THRESHOLD AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'yellow' WHEN dgl.allocated_value < b.LOW_THRESHOLD_TOP AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'red' WHEN dgl.allocated_value < b.LOW_THRESHOLD_MID AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'orange' WHEN dgl.allocated_value < b.LOW_THRESHOLD AND dgl.concept_code = 'Acc_Imbalance_Inventory_Operator' THEN 'yellow' ELSE 'NO_WARN' END AS colour, b.mode_code FROM data_global dgl LEFT JOIN base_intraday_setup b ON dgl.idn_allocation = b.idn_allocation AND b.idn_zone = dgl.idn_zone AND dgl.gas_day = b.gas_day ) PIVOT(SUM(allocated_value) AS val, MIN(colour) AS warn, MIN(mode_code) AS mode_code FOR aggregation IN ('EAST#Accumulated_Total_Inventory' AS e_total_Inventory, 'WEST#Accumulated_Total_Inventory' AS w_total_Inventory, 'EAST#Base_Inventory' AS e_base_Inventory, 'WEST#Base_Inventory' AS w_base_Inventory, 'EAST#Acc_Imbalance_Inventory_Operator' AS e_inventory_operator, 'WEST#Acc_Imbalance_Inventory_Operator' AS w_inventory_operator, 'EAST#Acc_Imbalance_Others' AS e_other_shippers, 'WEST#Acc_Imbalance_Others' AS w_other_shippers, 'EAST#Acc_Imbalance_Inventory_Others' AS e_others, 'WEST#Acc_Imbalance_Inventory_Others' AS w_others, 'EAST#Acc_Imbalance_Inventory_PTTShipper' AS e_pttshipper, 'WEST#Acc_Imbalance_Inventory_PTTShipper' AS w_pttshipper) )

mapper.delete:
DELETE tpa_tallocation_shipper_filter
  WHERE gas_day = #{gasday}
    AND page_code = 'INTR_ACC_IMBALANCE_INVENTORY'
