mapper.select:
		select trc.idn_resbal_contract as key, trc.contract_code as value
		  from tpa_tresbal_contract trc
		  where
          trc.idn_pipeline_system = #{idnSystem}

			and trc.idn_user_group = #{shipperId}

		 order by UPPER(trc.contract_code)

SELECT a.idn_area as key, a.area_code as value FROM tpa_varea_tpa a, tpa_vzone_tpa z WHERE a.idn_zone = z.idn_zone AND z.idn_pipeline_system = (SELECT tps.idn_pipeline_system FROM tpa_tpipeline_system tps WHERE tps.pipeline_system_code = #{systemCode})
AND z.idn_zone = #{zoneId}
ORDER BY UPPER(a.area_code)

		select tsp.idn_system_point as key, tsp.point_code as value
		  from tpa_tsystem_point tsp,
		       tpa_tarea ta, tpa_tzone tz
		 where tsp.idn_system_point_group =
		       (select tpgr.idn_system_point_group
		          from tpa_tsystem_point_group tpgr
		         where tpgr.group_code = 'NOMINATION')
		   and ta.idn_area = tsp.idn_area

		   and ta.idn_zone = #{zoneId}


		   and tsp.idn_system_point_type = #{pointTypeId}



		   and ta.idn_area = #{areaId}

   and tz.idn_zone = ta.idn_zone
   and tz.idn_pipeline_system = #{idnSystem}
		   and trunc(sysdate) between tsp.start_date and nvl(tsp.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))
		order by tsp.point_code

select * from (select trc.idn_resbal_contract, trc.contract_code, trc.idn_user_group, tgr.user_group_id, trc.operator_comments, trc.file_name from tpa_tresbal_contract trc, tpa_tuser_group tgr where tgr.idn_user_group = trc.idn_user_group
and trc.idn_resbal_contract = #{resBalGasContractId} and trc.idn_pipeline_system = #{idnSystem}
and trc.idn_user_group = #{shipperId}
) trc2 join (select tdet.idn_resbal_contract_det, tdet.idn_resbal_contract as idn_resbal_contract_from_det, tdet.idn_system_point, tpo.point_code, tpty.idn_system_point_type, tpty.type_code, tar.idn_zone, tz.zone_code, tdet.start_date, tdet.end_date, tdet.daily_reserve from tpa_tresbal_contract_det tdet, tpa_tsystem_point tpo, tpa_tsystem_point_type tpty, tpa_tarea tar, tpa_tzone tz where tpo.idn_system_point = tdet.idn_system_point and tpty.idn_system_point_type = tpo.idn_system_point_type and tar.idn_area = tpo.idn_area and tz.idn_zone = tar.idn_zone and tz.idn_pipeline_system = #{idnSystem}
and nvl(tdet.end_date, to_date('31/12/9999', 'DD/MM/YYYY')) >= #{startDate}
and #{endDate} >= tdet.start_date
and tar.idn_zone = #{zoneId}
) tdet2 on tdet2.idn_resbal_contract_from_det = trc2.idn_resbal_contract order by upper(trc2.contract_code) asc, upper(tdet2.zone_code) asc, tdet2.start_date asc, upper(tdet2.point_code) asc

select trbc.contract_document
  from tpa_tresbal_contract trbc
 where trbc.idn_resbal_contract =#{contractId}
