mapper.select:
select IDN_VAL_AVAIL_CAPACITY as idn_val_avail_capacity, AREA as area, AVAILABLE_CAPACITY_VALUE as available_cap, START_DATE as monthYear, END_DATE as endDate
from TPA_TVALUES_AVAILABLE_CAPACITY where area = #{area}
and idn_val_avail_capacity = #{idn_val_avail_capacity}

mapper.update:
UPDATE TPA_TVALUES_AVAILABLE_CAPACITY set
	available_capacity_value = #{available_cap},
	start_date=#{monthYear},
	end_date=#{endDate},
	aud_last_user=#{username}
WHERE idn_val_avail_capacity = #{idn_val_avail_capacity} 
	and area=#{area}
