mapper.select:
select t1.idn_contract_request as idn_contract_request, t1.request_code as request_code, t1.arriving_date as arriving_date, t1.idn_user_group as idn_user_group, t1.user_group_id as user_group_id, t1.user_group_name as user_group_name, t1.short_name as short_name, t1.idn_operation as idn_operation, t1.operation_desc as operation_desc, t2.idn_contract as idn_contract, t2.contract_code as contract_code, t1.status as status, t1.start_date as start_date, t1.end_date as end_date, t1.idn_operation_file as idn_operation_file, t1.file_name as file_name, t1.shipper_warnings as shipper_warnings, t1.operator_comments as operator_comments, t2.shadow_time as shadow_time, t2.shadow_period as shadow_period, t1.response_date as response_date, t1.idn_pipeline_system as idn_pipeline_system from (select tcreq.idn_contract_request as idn_contract_request, tcreq.request_code as request_code, tcreq.arriving_date as arriving_date, tuser_group.idn_user_group as idn_user_group, tuser_group.user_group_id as user_group_id, tuser_group.user_group_name as user_group_name, tuser_group.short_name as short_name, toper.idn_operation as idn_operation, toper.operation_desc as operation_desc, tcreq.idn_contract as idn_contract, tcreq.status as status, tcreq.start_date as start_date, tcreq.end_date as end_date, tfile.idn_operation_file as idn_operation_file, tfile.file_name as file_name, tcreq.shipper_warnings as shipper_warnings, tcreq.operator_comments as operator_comments, tcreq.response_date as response_date, tcreq.idn_pipeline_system as idn_pipeline_system from (SELECT tcreq2.* FROM tpa_tcontract_request tcreq2, tpa_tcontract tcontr2 WHERE tcontr2.idn_contract (+)= tcreq2.idn_contract AND NVL(tcontr2.is_grandfathering(+), 'N') = 'N' AND tcreq2.idn_pipeline_system = #{idn_system} AND tcontr2.idn_pipeline_system(+) = tcreq2.idn_pipeline_system ) tcreq, tpa_tuser_group tuser_group, tpa_toperation toper, tpa_toperation_file tfile where tcreq.is_capacity_release='N'
and tcreq.request_code like #{capacityRequestCode}
and tuser_group.idn_user_group = #{shipperId}
#{item}
and toper.idn_operation in (-1)
#{item}
and tcreq.status in ('no_valid_status')
and trunc(tcreq.arriving_date) >= #{startDate}
and #{endDate} >= trunc(tcreq.arriving_date)
and tcreq.idn_user_group=tuser_group.idn_user_group and tcreq.idn_operation=toper.idn_operation and tfile.idn_operation_file (+)= tcreq.idn_operation_file) t1 left outer join(select tcontr.idn_contract as idn_contract, tcontr.contract_code as contract_code, tsha.shadow_time as shadow_time, tsha.shadow_period as shadow_period from tpa_tcontract tcontr, tpa_tcontract_shadow tsha where tsha.idn_contract=tcontr.idn_contract and tcontr.idn_pipeline_system = #{idn_system}) t2 on t2.idn_contract=t1.idn_contract order by t1.arriving_date desc, t1.user_group_id asc, t2.contract_code asc

