mapper.select:
select tcreq.idn_contract_request as idn_contract_request, tcreq.arriving_date as arriving_date, tusergr.idn_user_group as idn_user_group, tusergr.user_group_id as user_group_id, tusergr.user_group_name as user_group_name, top.idn_operation_file as idn_operation_file, top.file_name as file_name from tpa_tcontract_request tcreq, tpa_tuser_group tusergr, tpa_toperation_file top where tcreq.status = 'SUBMITTED'
and tcreq.idn_user_group = #{shipperId}
and tcreq.idn_contract_request in (select distinct(tagr.idn_contract_request) from tpa_tcontract_agreement tagr, tpa_tcontract_point tp where tp.idn_contract_agreement = tagr.idn_contract_agreement and tp.is_new_connection = 'Y') and tcreq.idn_pipeline_system = #{idn_system} and tusergr.idn_user_group = tcreq.idn_user_group and top.idn_operation_file = tcreq.idn_operation_file and trunc(sysdate) between tusergr.start_date and nvl(tusergr.end_date, to_date('31/12/9999', 'DD/MM/YYYY')) order by tcreq.arriving_date desc

select binary_data 
from tpa_toperation_file
where idn_operation_file=#{opFileId}

select tsh.erc_license_id
from tpa_tshipper tsh
where tsh.idn_user_group = #{shipperId}
