mapper.select:
select t1.request_code as request_code, t1.idn_contract_request as idn_contract_request, t1.arriving_date as arriving_date, t1.idn_user_group as idn_user_group_from, t1.user_group_id as user_group_id_from, t1.short_name as short_name, t1.idn_contract as idn_contract_from, t1.contract_code as contract_code_from, t1.status as status, t1.is_released_to_operator as is_released_to_operator, t2.idn_user_group as idn_user_group_to, t2.user_group_id as user_group_id_to, t2.idn_contract as idn_contract_to, t2.contract_code as contract_code_to from (select treq.request_code, treq.idn_contract_request, treq.arriving_date, treq.idn_user_group, tugr.user_group_id, tugr.short_name, treq.idn_contract, tcon.contract_code, treq.status, treq.is_released_to_operator, t3.released_to from tpa_tcontract_request treq, tpa_tuser_group tugr, tpa_tcontract tcon, (select distinct tagree.idn_contract_request, tcpoint.received_from, tcpoint.released_to from tpa_tcontract_agreement tagree, tpa_tcontract_point tcpoint where tcpoint.idn_contract_agreement=tagree.idn_contract_agreement) t3 where treq.is_capacity_release='Y'
and treq.idn_user_group=#{shipperId}
and treq.status=#{statusCode}
and treq.status!='ACCEPTED' and treq.idn_pipeline_system = #{idn_system} and tcon.idn_pipeline_system = treq.idn_pipeline_system and tugr.idn_user_group=treq.idn_user_group and trunc(sysdate) between tugr.start_date and nvl(tugr.end_date, to_date('31/12/9999', 'DD/MM/YYYY')) and tcon.idn_contract=treq.idn_contract and t3.idn_contract_request=treq.idn_contract_request and t3.received_from is null) t1 left outer join (select distinct tcon2.idn_user_group, tugr2.short_name, tugr2.user_group_id, tcon2.idn_contract, tcon2.contract_code from tpa_tcontract tcon2, tpa_tuser_group tugr2 where tugr2.idn_user_group=tcon2.idn_user_group and trunc(sysdate) between tugr2.start_date and nvl(tugr2.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))) t2 on t2.idn_contract=t1.released_to order by t1.arriving_date asc

mapper.update:
	update tpa_tcontract_point set
       released_to = #{releasedToId},
       aud_last_user = #{username}
    where idn_contract_point=#{contractPointId}
