mapper.select:
SELECT a.idn_area idn_area, a.area_code area_code, a.area_desc area_desc, nvl(gq.idn_area_gas_quality, -1) idn_area_gas_quality, nvl(gq.start_date, a.START_DATE) start_date_gas_Quality, ug.idn_user_group idn_user_group, ug.user_group_id user_group_id, ug.user_group_name user_group_name, #{parameterCode1} as parameterCode1, #{parameterCode2} as parametercode2 FROM TPA_Varea_TPA a, tpa_tpipeline_system ps, tpa_tarea_shipper_gasq gq, tpa_tuser_group ug WHERE gq.idn_area = a.idn_area AND gq.idn_user_group = ug.idn_user_group AND nvl(gq.end_date, gq.start_date) >= gq.start_date AND gq.version_date =(SELECT MAX(gqx.version_date) FROM tpa_tarea_shipper_gasq gqx WHERE gq.idn_area = gqx.idn_area AND gq.idn_user_group = gqx.idn_user_group AND gq.start_date = gqx.start_date) AND ps.idn_pipeline_system = #{idn_system}
AND a.idn_area = #{idn_area}
AND ug.idn_user_group = #{idn_shipper}
order by UPPER (area_code), start_date_gas_Quality DESC

mapper.update:
UPDATE tpa_tarea_shipper_gasq 
SET start_date=#{start_date_gas_Quality}, 
aud_last_user=#{username} 
WHERE idn_area_gas_quality=#{idn_area_gas_quality}
