mapper.select:
SELECT o.idn_operation idn_operation, o.operation_desc operation_desc, o.idn_operation_category idn_operation_category, o.category_code category_code, o.idn_operation_term idn_operation_term, o.term_code term_code, dl.idn_deadline_limit idn_deadline_limit, dt.idn_deadline_type idn_deadline_type, dt.deadline_desc deadline_desc, dt.deadline_type deadline_type, od.idn_operation_deadline idn_operation_deadline, od.start_date start_date, od.DAY_LIMIT dayLimit, od.MONTH_LIMIT monthLimit, od.MONTHS_BEFORE monthsBefore, od.YEARS_BEFORE yearsBefore FROM tpa_voperation o, tpa_toperation_deadline od, tpa_tdeadline_limit dl, tpa_tdeadline_type dt WHERE o.idn_operation = od.idn_operation AND o.category_code = 'CONTRACT' AND od.idn_deadline_type = dt.idn_deadline_type AND od.idn_deadline_limit = dl.idn_deadline_limit AND dl.limit_code = 'MAX' AND nvl(od.end_date, od.start_date) >= od.start_date AND od.version_date = (SELECT MAX(odx.version_date) FROM tpa_toperation_deadline odx WHERE odx.idn_operation = od.idn_operation AND odx.idn_deadline_type = od.idn_deadline_type AND odx.idn_deadline_limit = od.idn_deadline_limit AND odx.start_date = od.start_date) AND od.is_enabled = 'Y'
AND dt.idn_deadline_type = #{processType}
AND o.IDN_OPERATION_TERM = #{type}
AND nvl(od.end_date,nvl((SELECT MIN (ody.start_date) - 1 FROM tpa_toperation_deadline ody WHERE ody.idn_operation = od.idn_operation AND ody.idn_deadline_type = od.idn_deadline_type AND ody.idn_deadline_limit = od.idn_deadline_limit AND ody.start_date > od.start_date AND nvl(ody.end_date, ody.start_date) >= ody.start_date AND ody.is_enabled = 'Y' AND ody.version_date = (SELECT MAX(odz.version_date) FROM tpa_toperation_deadline odz WHERE odz.idn_operation = ody.idn_operation AND odz.idn_deadline_type = ody.idn_deadline_type AND odz.idn_deadline_limit = ody.idn_deadline_limit AND odz.start_date = ody.start_date)) , to_date('9999/12/31','yyyy/mm/dd')))>= #{startDate}
and #{endDate} >= od.start_date
ORDER BY UPPER (o.TERM_CODE), dt.execution_order, od.start_date DESC

mapper.update:
UPDATE tpa_toperation_deadline set
	   end_date = #{endDate},
	   aud_last_user=#{username}
WHERE idn_operation_deadline = #{idn_operation_deadline}

mapper.select:
	SELECT od.start_date start_date
    FROM   tpa_toperation_deadline od
	WHERE  idn_operation_deadline = #{idn_operation_deadline}
