mapper.select:
with cont_nom_point as ( select tcnp.idn_contract as idn_contract, ( select idn_system_point from tpa_tsystem_point where idn_system_point in ( select idn_contract_point from tpa_tsystem_point_param where idn_nomination_point = tcnp.idn_nom_point)) as idn_contract_point, u.user_group_id as shipper, u.idn_user_group as idn_shipper, tc.contract_code as contract_id, tc.start_date, tc.end_date, tcnp.active_date_from as start_date_active, tcnp.active_date_to as end_date_active from tpa_tcontract_nom_point tcnp, tpa_tcontract tc, tpa_tuser_group u where tcnp.idn_contract = tc.idn_contract and tc.idn_user_group = u.idn_user_group
and tcnp.idn_contract = #{idn_contract}
and idn_nom_point = #{idn_nomination_point}
and nvl(tc.end_date, to_date('9999/12/31', 'yyyy/mm/dd'))>= #{startDate}
and #{endDate} >= tc.start_date
and tc.idn_user_group = #{idn_shipper}
) select * from cont_nom_point tcnp
where tcnp.idn_contract_point = #{idn_contract_point}
group by tcnp.idn_contract, tcnp.idn_contract_point, tcnp.shipper, tcnp.idn_shipper, tcnp.contract_id, tcnp.start_date, tcnp.end_date , tcnp.start_date_active, tcnp.end_date_active order by tcnp.idn_contract, tcnp.start_date

		select t.idn_contract as key,
	       t.CONTRACT_code as value
 		from TPA_TCONTRACT t
		where 1 = 1

			and t.idn_user_group = #{idn_shipper}

		and t.idn_pipeline_system = #{idn_system}

		    AND nvl(t.end_date,to_date('9999/12/31','yyyy/mm/dd'))>=  #{startDate}


		    and 1=0


		      AND #{endDate} >= t.start_date


		    and 1=0

SELECT t.idn_contract AS KEY, t.CONTRACT_code AS value FROM TPA_TCONTRACT t, TPA_TUSER_GROUP u WHERE 1 = 1 AND t.idn_user_group = u.idn_user_group AND t.idn_user_group = #{idn_shipper}
AND nvl(t.end_date, to_date('9999/12/31', 'yyyy/mm/dd'))>= #{startDate}
AND #{endDate} >= t.start_date

select t1.idn_system_point as key, t1.point_code as value from tpa_tsystem_point t1 where 1 = 1
AND nvl(t1.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >= #{startDate}
AND #{endDate} >= t1.start_date
and t1.idn_system_point in (select idn_contract_point from tpa_tsystem_point_param t2 where 1 = 1
AND nvl(t2.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >= #{startDate}
AND #{endDate} >= t2.start_date
and idn_contract_point in (select distinct idn_system_point from TPA_TCONTRACT_POINT t3 where 1 = 1 and t3.idn_contract_agreement in (select idn_contract_agreement from tpa_tcontract_agreement t6 where 1 = 1
AND nvl(t6.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >= #{startDate}
AND #{endDate} >= t6.start_date
and t6.idn_contract_request in (select idn_contract_request from tpa_tcontract_request t4 where 1 = 1
AND nvl(t4.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >= #{startDate}
AND #{endDate} >= t4.start_date
and t4.idn_contract in (select idn_contract --idn_contract_request from tpa_tcontract t5 where 1 = 1
AND nvl(t5.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >=#{startDate}
AND #{endDate} >= t5.start_date
and t5.idn_contract = #{idn_contract} and t5.idn_pipeline_system = #{idn_system} ) ) ) ) ) ORDER BY t1.point_code

SELECT count(*) FROM TPA_TCONTRACT_NOM_POINT t WHERE t.idn_contract = #{idn_contract}
and t.idn_nom_point in
#{item}
AND (#{startDate} BETWEEN t.active_date_from AND t.active_date_to OR #{endDate} BETWEEN t.active_date_from AND t.active_date_to)

mapper.update:
UPDATE TPA_TCONTRACT_NOM_POINT set
	IDN_NOM_POINT = #{idn_nomination_point},
	aud_last_user = #{username}
WHERE idn_contract_nom_point = #{idn_contract_nom_point}

mapper.select:
	    SELECT t.IDN_CONTRACT_NOM_POINT
    FROM TPA_TCONTRACT_NOM_POINT t
    WHERE t.IDN_CONTRACT = #{idn_contract}
          AND  t.IDN_NOM_POINT = #{idn_nomination_point}
