mapper.select:
SELECT p.group_code, p.idn_system_point_group, p.point_code point_code, p.point_desc point_desc, p.type_desc type_desc, ps.pipeline_system_code pipeline_system_code, z.zone_code zone_code, a.area_code area_code, p.start_date, p.end_date, p.idn_system_point idn_system_point, p.idn_system_point_type idn_system_point_type, p.idn_system_point_group idn_system_point_group, ps.idn_pipeline_system idn_pipeline_system, z.idn_zone idn_zone, a.idn_area idn_area FROM tpa_vsystem_point p, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps WHERE p.group_code = 'CONTRACT' AND a.idn_area = p.idn_area AND a.idn_zone = z.idn_zone AND z.idn_pipeline_system = ps.idn_pipeline_system AND nvl(p.end_date, p.start_date) >= p.start_date
AND nvl(p.end_date,to_date('9999/12/31','yyyy/mm/dd'))>= #{startDate}
AND #{endDate} >= p.start_date
AND p.idn_system_point = #{id}
AND p.idn_system_point_type = #{pointType}
AND ps.idn_pipeline_system = #{idn_system}
AND z.idn_zone = #{zone}
AND a.idn_area = #{area}
ORDER BY UPPER (p.point_code)

mapper.update:
UPDATE tpa_tsystem_point_param set
	end_date = #{endDate},
	aud_last_user=#{username}
WHERE idn_system_point = #{idn_system_point}
      AND idn_system_point_param = #{idn_system_point_param}
