mapper.select:
SELECT tp.idn_system_point idn_system_point, pp.METERING_ID metering_id, tp.POINT_CODE point_code, tp.POINT_DESC point_desc, tp.TYPE_DESC type_desc, pp.NOMINAL_CAPACITY nominal_capacity, pp.MIN_PRESSURE min_pressure, pp.MAX_PRESSURE max_pressure, ct.TYPE_DESC customer_type_desc, nomp.point_code point_code_nomination, conp.point_code point_code_contract, qual.point_code point_code_quality, tp.LATITUDE latitude, tp.LONGITUDE longitude, ps.PIPELINE_SYSTEM_CODE pipeline_system_code, z.ZONE_CODE zone_code, a.AREA_CODE area_code, sub.SUBAREA_CODE subarea_code, pp.CON_PARTY_PHONE con_party_phone, pp.CON_PARTY_NAME con_party_name, pp.CON_PARTY_EMAIL con_party_email, pp.start_date, pp.end_date, a.IDN_AREA idn_area, sub.idn_subarea idn_subarea, tp.IDN_SYSTEM_POINT_GROUP idn_system_point_group, tp.IDN_SYSTEM_POINT_TYPE idn_system_point_type, z.IDN_ZONE idn_zone, ps.IDN_PIPELINE_SYSTEM idn_pipeline_system, ct.idn_customer_type idn_customer_type, nomp.idn_system_point idn_system_point_nomination, conp.idn_system_point idn_system_point_contract, qual.idn_system_point idn_system_point_quality, pp.idn_system_point_param idn_system_point_param FROM tpa_vsystem_point tp, tpa_tsystem_point_param pp, tpa_tcustomer_type ct, tpa_tsystem_point nomp, tpa_tsystem_point conp, tpa_tsystem_point qual, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps, tpa_tsubarea sub WHERE tp.idn_system_point = pp.idn_system_point AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND nomp.idn_system_point (+)= pp.idn_nomination_point AND conp.idn_system_point (+)= pp.idn_contract_point AND qual.idn_system_point (+)= pp.idn_quality_point AND sub.IDN_SUBAREA (+) = pp.IDN_SUBAREA AND a.idn_area = nomp.IDN_AREA AND a.IDN_ZONE = z.IDN_ZONE AND z.IDN_PIPELINE_SYSTEM = ps.IDN_PIPELINE_SYSTEM AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date)
AND nvl(tp.end_date,to_date('9999/12/31','yyyy/mm/dd'))>= #{startDate} AND nvl(pp.end_date, nvl((SELECT MIN(ppy.start_date) - 1 FROM tpa_tsystem_point_param ppy WHERE ppy.idn_system_point = pp.idn_system_point AND ppy.start_date > pp.start_date AND nvl(ppy.end_date, ppy.start_date) >= ppy.start_date AND ppy.version_date = (SELECT max(ppz.version_date) FROM tpa_tsystem_point_param ppz WHERE ppz.idn_system_point = ppy.idn_system_point AND ppz.start_date = ppy.start_date)), to_date('9999/12/31','yyyy/mm/dd')))>= #{startDate}
AND #{endDate} >= tp.start_date AND #{endDate} >= pp.start_date
AND tp.idn_system_point = #{id}
AND tp.idn_system_point_type = #{pointType}
AND ps.idn_pipeline_system = #{idn_system}
AND z.idn_zone = #{zone}
AND a.idn_area = #{area}
ORDER BY UPPER (metering_id)

    select spp.idn_system_point_param as idn_system_point
		from tpa_tsystem_point_param spp		
		where spp.idn_system_point = #{idn_system_point}		
		  and spp.idn_subarea = #{idn_subarea}		
		  and spp.idn_contract_point = #{idn_system_point_contract}		
		  and spp.idn_nomination_point = #{idn_system_point_nomination}		
		  and spp.idn_quality_point = #{idn_system_point_quality}		
		  and spp.idn_customer_type = #{idn_customer_type}		
		  and spp.start_date  <  = #{startDate}		
		  and (spp.end_date is null or spp.end_date = '')

SELECT tp.idn_system_point idn_system_point, pp.METERING_ID metering_id, tp.POINT_CODE point_code, tp.POINT_DESC point_desc, tp.TYPE_DESC type_desc, pp.NOMINAL_CAPACITY nominal_capacity, pp.MIN_PRESSURE min_pressure, pp.MAX_PRESSURE max_pressure, ct.TYPE_DESC customer_type_desc, nomp.point_code point_code_nomination, conp.point_code point_code_contract, qual.point_code point_code_quality, tp.LATITUDE latitude, tp.LONGITUDE longitude, ps.PIPELINE_SYSTEM_CODE pipeline_system_code, z.ZONE_CODE zone_code, a.AREA_CODE area_code, sub.SUBAREA_CODE subarea_code, pp.CON_PARTY_PHONE con_party_phone, pp.CON_PARTY_NAME con_party_name, pp.CON_PARTY_EMAIL con_party_email, pp.start_date,
nvl(pp.end_date, nvl((SELECT MIN(ppy.start_date) - 1 FROM tpa_tsystem_point_param ppy WHERE ppy.idn_system_point = pp.idn_system_point AND ppy.start_date > pp.start_date AND nvl(ppy.end_date, ppy.start_date) >= ppy.start_date AND ppy.version_date = (SELECT max(ppz.version_date) FROM tpa_tsystem_point_param ppz WHERE ppz.idn_system_point = ppy.idn_system_point AND ppz.start_date = ppy.start_date)), '')) as end_date, pp.version_date,
a.IDN_AREA idn_area, sub.idn_subarea idn_subarea, tp.IDN_SYSTEM_POINT_GROUP idn_system_point_group, tp.IDN_SYSTEM_POINT_TYPE idn_system_point_type, z.IDN_ZONE idn_zone, ps.IDN_PIPELINE_SYSTEM idn_pipeline_system, ct.idn_customer_type idn_customer_type, nomp.idn_system_point idn_system_point_nomination, conp.idn_system_point idn_system_point_contract, qual.idn_system_point idn_system_point_quality, pp.idn_system_point_param idn_system_point_param FROM tpa_vsystem_point tp, tpa_tsystem_point_param pp, tpa_tcustomer_type ct, tpa_tsystem_point nomp, tpa_tsystem_point conp, tpa_tsystem_point qual, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps, tpa_tsubarea sub WHERE tp.idn_system_point = pp.idn_system_point AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND nomp.idn_system_point (+)= pp.idn_nomination_point AND conp.idn_system_point (+)= pp.idn_contract_point AND qual.idn_system_point (+)= pp.idn_quality_point AND sub.IDN_SUBAREA (+) = pp.IDN_SUBAREA AND a.idn_area = tp.IDN_AREA AND a.IDN_ZONE = z.IDN_ZONE AND z.IDN_PIPELINE_SYSTEM = ps.IDN_PIPELINE_SYSTEM
AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date)
AND nvl(tp.end_date,to_date('9999/12/31','yyyy/mm/dd'))>= #{startDate} AND nvl(pp.end_date, nvl((SELECT MIN(ppy.start_date) - 1 FROM tpa_tsystem_point_param ppy WHERE ppy.idn_system_point = pp.idn_system_point AND ppy.start_date > pp.start_date AND nvl(ppy.end_date, ppy.start_date) >= ppy.start_date AND ppy.version_date = (SELECT max(ppz.version_date) FROM tpa_tsystem_point_param ppz WHERE ppz.idn_system_point = ppy.idn_system_point AND ppz.start_date = ppy.start_date)), to_date('9999/12/31','yyyy/mm/dd')))>= #{startDate}
AND #{endDate} >= tp.start_date AND #{endDate} >= pp.start_date
AND tp.idn_system_point = #{id}
AND tp.idn_system_point_type = #{pointType}
AND ps.idn_pipeline_system = #{idn_system}
AND z.idn_zone = #{zone}
AND a.idn_area = #{area}
ORDER BY UPPER (metering_id)

mapper.update:
update tpa_tsystem_point_param spp
 set spp.end_date = #{startDate}-1
 where spp.idn_system_point_param = #{idn_system_point}

UPDATE tpa_tmoc_metering_id set
	is_assigned = 'Y',
	aud_last_user=#{username}
WHERE metering_id = #{meteringID}

UPDATE tpa_tsystem_point_param set
	   end_date = #{endDate},
	   aud_last_user=#{username}
WHERE  idn_system_point = #{idn_system_point}
       AND idn_system_point_param = #{idn_system_point_param}

mapper.select:
	SELECT  pp.start_date
    FROM tpa_tsystem_point_param pp
	WHERE idn_system_point = #{idn_system_point}
	      AND idn_system_point_param = #{idn_system_point_param}

WITH 
point_relation AS
(SELECT spr.contract_point_idn,
         spr.nomination_point_idn
    FROM tpa_vsystem_point_relation spr
   WHERE spr.min_end_date >= #{startDate} 
     AND NVL(#{endDate}, spr.max_start_date) >= spr.max_start_date 
     AND spr.metered_point_idn != #{idn_system_point}
   GROUP BY spr.contract_point_idn, spr.nomination_point_idn)
SELECT pr.contract_point_idn
  FROM point_relation pr
WHERE pr.nomination_point_idn = #{idn_system_point_nomination}
   AND pr.contract_point_idn != #{idn_system_point_contract}
