mapper.select:
SELECT ct.idn_customer_type as key, ct.TYPE_DESC as value FROM tpa_vsystem_point tp, tpa_tsystem_point_param pp, tpa_tcustomer_type ct, tpa_tsystem_point nomp, tpa_tsystem_point conp, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps, tpa_tsubarea sub WHERE tp.idn_system_point = pp.idn_system_point AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND nomp.idn_system_point (+)= pp.idn_nomination_point AND conp.idn_system_point (+)= pp.idn_contract_point AND sub.IDN_SUBAREA (+) = pp.IDN_SUBAREA AND a.idn_area = nomp.IDN_AREA AND a.IDN_ZONE = z.IDN_ZONE AND z.IDN_PIPELINE_SYSTEM = ps.IDN_PIPELINE_SYSTEM AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date) AND ps.idn_pipeline_system = #{idnSystem}
AND tp.idn_system_point = #{idnMeteringPoint}
group by ct.idn_customer_type, ct.TYPE_DESC ORDER BY UPPER (value)

SELECT conp.idn_system_point as key , conp.point_code as value FROM tpa_vsystem_point tp, tpa_tsystem_point_param pp, tpa_tcustomer_type ct, tpa_tsystem_point nomp, tpa_tsystem_point conp, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps, tpa_tsubarea sub WHERE tp.idn_system_point = pp.idn_system_point AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND nomp.idn_system_point (+)= pp.idn_nomination_point AND conp.idn_system_point (+)= pp.idn_contract_point AND sub.IDN_SUBAREA (+) = pp.IDN_SUBAREA AND a.idn_area = nomp.IDN_AREA AND a.IDN_ZONE = z.IDN_ZONE AND z.IDN_PIPELINE_SYSTEM = ps.IDN_PIPELINE_SYSTEM AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date) AND ps.idn_pipeline_system = #{idnSystem}
AND tp.idn_system_point = #{idnMeteringPoint}
GROUP BY conp.idn_system_point, conp.point_code ORDER BY UPPER (value)

with ship_met_point as (select mt.IDN_SHIPPER_MET_POINT, mt.idn_user_group, u.user_group_id, mt.active_date_from, mt.active_date_to, (select idn_system_point from tpa_tsystem_point where idn_system_point in (select idn_system_point from tpa_tsystem_point_param where idn_system_point = mt.idn_met_point)) as idn_met_point from tpa_tshipper_met_point mt, Tpa_Tuser_Group u
where mt.idn_user_group = u.idn_user_group
AND nvl(mt.active_date_to ,to_date('9999/12/31','yyyy/mm/dd'))>= #{startDate}
AND #{endDate} >= mt.active_date_from
AND mt.idn_user_group = #{idnShipper}
) select * from ship_met_point mt group by mt.IDN_SHIPPER_MET_POINT, mt.idn_user_group, mt.user_group_id, mt.active_date_from, mt.active_date_to, mt.idn_met_point order by mt.idn_user_group, mt.active_date_from

select to_number(tp.idn_system_point || ct.idn_customer_type || conp.idn_system_point) as compositeKey, tsmp.idn_shipper_met_point as idn_shipper_met_point, tsmp.idn_met_point, tp.POINT_CODE, tsmp.idn_user_group as idn_user_group, ct.idn_customer_type as idn_customer_type, ct.TYPE_DESC as TYPE_DESC , conp.idn_system_point as idn_GROUP_ID , conp.point_code as GROUP_ID
from TPA_TSHIPPER_MET_POINT tsmp, TPA_TUSER_GROUP u, tpa_tcustomer_type ct, tpa_tsystem_point conp, tpa_tsystem_point_param pp, tpa_vsystem_point tp where tsmp.idn_user_group = u.idn_user_group and tp.idn_system_point = pp.idn_system_point and tsmp.idn_met_point = tp.IDN_SYSTEM_POINT AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND conp.idn_system_point (+)= pp.idn_contract_point AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date)
and tsmp.idn_user_group = #{idnShipper}
and tsmp.idn_met_point = #{idnMeteringPoint}
group by to_number(tp.idn_system_point || ct.idn_customer_type || conp.idn_system_point), tsmp.idn_shipper_met_point, tsmp.idn_met_point, tp.POINT_CODE, tsmp.idn_user_group, ct.idn_customer_type, ct.TYPE_DESC, conp.idn_system_point, conp.point_code

SELECT to_number(tp.idn_system_point || ct.idn_customer_type || conp.idn_system_point) as compositeKey, tp.idn_system_point as IDN_MET_POINT, tp.POINT_CODE as POINT_CODE, ct.idn_customer_type as idn_customer_type, ct.TYPE_DESC as TYPE_DESC , conp.idn_system_point as idn_GROUP_ID , conp.point_code as GROUP_ID FROM tpa_vsystem_point tp, tpa_tsystem_point_param pp, tpa_tcustomer_type ct, tpa_tsystem_point nomp, tpa_tsystem_point conp, tpa_varea_tpa a, TPA_VZONE_TPA z, tpa_tpipeline_system ps, tpa_tsubarea sub WHERE tp.idn_system_point = pp.idn_system_point AND tp.group_code= 'METERED' AND ct.IDN_CUSTOMER_TYPE (+)= pp.IDN_CUSTOMER_TYPE AND nomp.idn_system_point (+)= pp.idn_nomination_point AND conp.idn_system_point (+)= pp.idn_contract_point AND sub.IDN_SUBAREA (+) = pp.IDN_SUBAREA AND a.idn_area = nomp.IDN_AREA AND a.IDN_ZONE = z.IDN_ZONE AND z.IDN_PIPELINE_SYSTEM = ps.IDN_PIPELINE_SYSTEM AND nvl(pp.end_date, pp.start_date) >= pp.start_date AND pp.version_date = (SELECT max(ppx.version_date) FROM tpa_tsystem_point_param ppx WHERE pp.idn_system_point = ppx.idn_system_point AND pp.start_date = ppx.start_date) AND ps.idn_pipeline_system = #{idnSystem}
AND tp.idn_system_point = #{idnMeteringPoint}
AND ct.idn_customer_type = #{idnCustomerType}
AND conp.idn_system_point = #{idnGroup}
order by tp.POINT_CODE

mapper.update:
		update TPA_TSHIPPER_MET_POINT smp
		set smp.ACTIVE_DATE_TO = #{endDate},    
		    smp.AUD_INS_USER = #{userName},
            smp.AUD_LAST_USER = #{userName}
		where smp.IDN_SHIPPER_MET_POINT = #{idnMeterdPointShipper}
