mapper.select:
SELECT sf.idn_shrinkage_factor as idn_shrinkage_factor, sf.idn_zone as idn_zone, z.zone_desc as zone_desc, z.zone_code as zone_code, sf.shrinkage_factor as shrinkage_factor, sf.start_date as start_date, sf.end_date as end_date FROM TPA_TSHRINKAGE_FACTOR sf, TPA_VZONE_TPA z WHERE sf.idn_zone= z.idn_zone AND nvl(sf.end_date, sf.start_date) >= sf.start_date AND sf.version_date = ( SELECT MAX(sfx.version_date) FROM TPA_TSHRINKAGE_FACTOR sfx WHERE sf.idn_zone = sfx.idn_zone AND sf.start_date = sfx.start_date)
AND z.idn_pipeline_system = #{idn_system}
AND nvl(sf.end_date, nvl((SELECT MIN(sfy.start_date) - 1 FROM TPA_TSHRINKAGE_FACTOR sfy WHERE sfy.idn_zone = sf.idn_zone AND sfy.start_date > sf.start_date AND nvl(sfy.end_date, sfy.start_date) >= sfy.start_date AND sfy.version_date = (SELECT MAX(sfz.version_date) FROM TPA_TSHRINKAGE_FACTOR sfz WHERE sfz.idn_zone = sfy.idn_zone AND sfz.start_date = sfy.start_date)), to_date('9999/12/31', 'yyyy/mm/dd'))) >= #{startDate} AND nvl(z.end_date, to_date('9999/12/31', 'yyyy/mm/dd')) >= #{startDate}
AND #{endDate} >= sf.start_date AND #{endDate} >= z.start_date
AND sf.idn_zone = #{zone}
order by UPPER (z.zone_code), sf.start_date DESC

mapper.update:
UPDATE tpa_tshrinkage_factor set
	end_date = #{endDate},
	aud_last_user=#{username}
WHERE idn_shrinkage_factor = #{idn_shrinkage_factor}

mapper.select:
	SELECT  sf.start_date as start_date
 	FROM tpa_tshrinkage_factor sf
	WHERE idn_shrinkage_factor = #{idn_shrinkage_factor}
