mapper.select:
select
idn_system_point AS key, point_code AS value
sp.idn_system_point AS key, sp.point_code AS value from tpa_tshipper_met_point p, tpa_tsystem_point sp where p.idn_user_group = #{idnShipper} and p.idn_met_point = sp.idn_system_point and TRUNC(sysdate) between p.active_date_from and p.active_date_to and idn_met_point in (SELECT idn_system_point
FROM ( SELECT tsp.idn_system_point, tsp.point_code FROM tpa_tsystem_point tsp WHERE tsp.idn_system_point_group = (SELECT tspgr.idn_system_point_group FROM tpa_tsystem_point_group tspgr WHERE tspgr.group_code = 'METERED')
#{item}
AND tsp.idn_area IN (-1)
UNION SELECT sp_met.idn_system_point, sp_met.point_code FROM tpa_tnom_concept_metering ncm, tpa_tsystem_point sp_met, tpa_tsystem_point_connect spc WHERE spc.idn_nomination_concept = ncm.idn_nomination_concept AND sp_met.idn_system_point = ncm.idn_system_point AND sp_met.idn_system_point_group = (SELECT tspgr.idn_system_point_group FROM tpa_tsystem_point_group tspgr WHERE tspgr.group_code = 'METERED')
AND (
#{item}
OR
#{item}
)
AND (-1) IN (spc.idn_area_to, spc.idn_area_from)
UNION SELECT sp_met.idn_system_point, sp_met.point_code FROM tpa_tsystem_point sp_met, tpa_tsystem_point_connect spc, tpa_tsystem_point_type spt WHERE spt.type_code = 'CONNECTION' AND sp_met.idn_system_point = spc.idn_system_point and spt.idn_system_point_type = sp_met.idn_system_point_type AND sp_met.idn_system_point_group = (SELECT tspgr.idn_system_point_group FROM tpa_tsystem_point_group tspgr WHERE tspgr.group_code = 'CONNECTION')
AND (
#{item}
OR
#{item}
)
AND (-1) IN (spc.idn_area_to, spc.idn_area_from)
UNION SELECT sp_met.idn_system_point, sp_met.point_code FROM tpa_tsystem_point sp_met, tpa_tsystem_point_connect spc, tpa_tsystem_point_type spt WHERE spt.type_code = 'CONCEPT_POINT' AND sp_met.idn_system_point = spc.idn_system_point and sp_met.idn_system_point_type = spt.idn_system_point_type AND sp_met.idn_system_point_group = (SELECT tspgr.idn_system_point_group FROM tpa_tsystem_point_group tspgr WHERE tspgr.group_code = 'CONNECTION') )
)

select distinct tmetin.idn_metering_input as key, tmetin.input_code as value from tpa_tmetering tmet, tpa_tmetering_input tmetin where tmetin.idn_metering_input = tmet.idn_metering_input
and trunc(tmet.gas_day) >= #{gasDayFrom}
and #{gasDayTo} >= trunc(tmet.gas_day)
#{item}
and tmet.idn_system_point in (-1)
order by upper(tmetin.input_code) desc

mapper.update:
		update tpa_twebservice_logger set
	       response_date = #{responseDate},
	       response_xml = #{responseXml},
	       status = #{status},
           aud_last_user=#{username}
	    where idn_webservice_logger=#{webserviceLogId}
