mapper.select:
select c.idn_contract as key, c.contract_code as value from tpa_tcontract c, tpa_tuser_group ug where c.idn_pipeline_system = #{idn_pipeline_system} and c.idn_user_group = ug.idn_user_group
and ug.idn_user_group = #{shipper_code}
and exists (select 1 from tpa_tcontract_consolidate cc where cc.idn_contract = c.idn_contract) and #{start_date} between c.start_date and c.end_date order by UPPER(value)

select c.idn_contract as key, c.contract_code as value from tpa_tcontract c, tpa_tuser_group ug where c.idn_pipeline_system = #{idn_pipeline_system} and c.idn_user_group = ug.idn_user_group
and ug.idn_user_group = #{shipper_code}
and exists (select 1 from tpa_tcontract_consolidate cc where cc.idn_contract = c.idn_contract) and (#{start_date} between c.start_date and c.end_date or #{end_date} between c.start_date and c.end_date) order by UPPER(value)

with warning_nom as ( select ndq.idn_nomination, max(greatest(ndq.IS_WARNED, nvl(nhq.Is_Warned,'N') , nvl(ngq.is_warned, 'N'))) as is_warned from tpa_vnomination_day_quantity ndq , tpa_vnomination_hour_quantity nhq , tpa_tnomination_gasq ngq where ndq.idn_nomination_entity = nhq.idn_nomination_entity(+) AND nhq.idn_nomination_day(+) = ndq.idn_nomination_day AND ndq.idn_nomination_day = nhq.idn_nomination_day(+) AND ndq.idn_nomination_entity = ngq.idn_nomination_entity(+) AND ndq.idn_nomination_concept = ngq.idn_nomination_concept(+) group by ndq.idn_nomination ) SELECT n.idn_nomination, n.nomination_code, n.start_date, n.nomination_version, c.idn_contract, c.contract_code, ug.idn_user_group, ug.user_group_id, ug.user_group_name, ug.short_name, n.is_responsed, n.shipper_comments, n.operator_comments, n.submission_comments, n.idn_shipper_file, (select file_name from tpa_toperation_file where n.idn_shipper_file = idn_operation_file) as shipper_file_name, n.idn_operator_file, (select file_name from tpa_toperation_file where n.idn_operator_file = idn_operation_file) as operator_file_name, n.is_valid, n.is_renomination as is_renomination, n.aud_ins_date as aud_ins_date FROM tpa_tnomination n, tpa_tuser_group ug, tpa_tuser_group_type ugt, tpa_voperation o, tpa_tcontract c, warning_nom w WHERE n.idn_nomination = w.idn_nomination AND n.idn_operation = o.idn_operation AND o.category_code = #{category_code} AND o.term_code = #{term_code} AND n.idn_user_group = ug.idn_user_group AND ug.idn_user_group_type = ugt.idn_user_group_type AND ugt.type_code = #{type_code} AND n.is_contracted = 'Y' AND n.idn_contract = c.idn_contract AND n.start_date = #{start_date} AND n.end_date = #{end_date} AND EXISTS (SELECT 1 FROM tpa_vnomination_entity ne WHERE ne.idn_nomination = n.idn_nomination AND ne.idn_pipeline_system = #{idn_pipeline_system} AND 1 >= ROWNUM) AND exists (select 1 from tpa_tnomination_day nd where nd.idn_nomination=n.idn_nomination) AND n.nomination_version = (SELECT MAX(nx.nomination_version) FROM tpa_tnomination nx WHERE nx.nomination_code = n.nomination_code AND nx.is_intraday = 'N') AND n.is_intraday = 'N'
and ug.idn_user_group = #{shipper_code}
and n.idn_contract = #{contract_code}
and w.is_warned = #{not_meet}
ORDER BY ug.user_group_id, c.contract_code
