mapper.select:
WITH best_operation AS (SELECT n.idn_nomination, n.idn_operation, n.idn_contract, CASE o.term_code WHEN 'DAILY' THEN 1 WHEN 'WEEKLY' THEN 2 END AS priority FROM tpa_tnomination n, tpa_voperation o WHERE n.is_contracted = 'Y'
AND trunc(n.end_date) >= #{startDate}
AND #{endDate} >= trunc(n.start_date)
AND n.nomination_version = (SELECT MAX(nx.nomination_version) FROM tpa_tnomination nx WHERE nx.nomination_code = n.nomination_code) AND NOT (n.is_responsed = 'Y' AND n.is_valid = 'N') AND n.idn_operation = o.idn_operation AND o.category_code = 'NOMINATION'), best_gas_q_values AS (SELECT n.idn_contract, ndq.gas_day, ne.idn_zone, ne.zone_code, gqp.parameter_code, SUM(ndq.day_matched_quantity) keep(dense_rank FIRST ORDER BY n.priority) AS day_matched_quantity, SUM(ndq.day_matched_quantity * ngq.quality_value) keep(dense_rank FIRST ORDER BY n.priority) AS quality_value_weighted, u.unit_desc FROM tpa_tgas_quality_param gqp, tpa_tunit u, best_operation n, tpa_vnomination_day_quantity ndq, tpa_vnomination_entity ne, tpa_vnomination_main_concepts nmc, tpa_tnomination_xls_headr nxh, tpa_tnomination_gasq ngq, tpa_tsystem_point_type spt WHERE gqp.parameter_code IN ('WI', 'HV', 'CO2') AND gqp.idn_unit = u.idn_unit AND n.idn_nomination = ndq.idn_nomination AND n.idn_nomination = ne.idn_nomination
AND trunc(ndq.gas_day) >= #{startDate}
AND #{endDate} >= trunc(ndq.gas_day)
AND ndq.idn_nomination_entity = ne.idn_nomination_entity AND ndq.idn_nomination_entity = nxh.idn_nomination_entity AND ndq.idn_nomination_concept = nmc.idn_nomination_concept AND ndq.idn_nomination_concept = nxh.idn_nomination_concept AND ne.idn_nomination_entity = nxh.idn_nomination_entity AND ne.idn_nomination_entity = ngq.idn_nomination_entity AND ne.is_point_entity = nmc.is_point_concept AND ne.is_area_entity = nmc.is_area_concept AND ne.is_zone_entity = nmc.is_zone_concept AND nmc.idn_nomination_concept = nxh.idn_nomination_concept AND nmc.idn_nomination_concept = ngq.idn_nomination_concept AND nxh.idn_nomination_entity = ngq.idn_nomination_entity AND nxh.idn_nomination_concept = ngq.idn_nomination_concept AND nxh.idn_system_point_type = spt.idn_system_point_type AND ngq.idn_gas_quality_param = gqp.idn_gas_quality_param AND spt.type_code = 'ENTRY'
#{item}
AND ne.idn_zone in (-1)
GROUP BY n.idn_contract, ndq.gas_day, ne.idn_zone, ne.zone_code, gqp.parameter_code, u.unit_desc) SELECT gas_day as start_date, idn_zone as idn_zone, zone_code as zone_code, parameter_code as parameter_code, decode(SUM(day_matched_quantity), 0, 0, round(SUM(quality_value_weighted) / SUM(day_matched_quantity), 2)) AS val, unit_desc as unit_desc FROM best_gas_q_values GROUP BY gas_day, idn_zone, zone_code, parameter_code, unit_desc ORDER BY gas_day, zone_code, parameter_code

SELECT n.idn_nomination as key, n.nomination_code as value FROM tpa_tnomination n, tpa_voperation o WHERE n.is_contracted = 'Y' AND n.nomination_version = (SELECT MAX(nx.nomination_version) FROM tpa_tnomination nx WHERE nx.nomination_code = n.nomination_code AND n.is_intraday = 'N') AND n.is_responsed = 'N' AND n.idn_operation = o.idn_operation AND o.category_code = 'NOMINATION' AND n.is_intraday = 'N'
AND trunc(n.end_date) >= #{startDate}
AND #{endDate} >= trunc(n.start_date)
