mapper.select:
select c.idn_contract as key, c.contract_code as value from tpa_tcontract c, tpa_tuser_group ug where c.idn_pipeline_system = #{systemId} and c.idn_user_group = ug.idn_user_group
and ug.idn_user_group = #{shipper_code}
and exists (select 1 from tpa_tcontract_consolidate cc where cc.idn_contract = c.idn_contract) and #{start_date} between c.start_date and c.end_date order by UPPER(value)

select c.idn_contract as key, c.contract_code as value from tpa_tcontract c, tpa_tuser_group ug where c.idn_pipeline_system = #{systemId} and c.idn_user_group = ug.idn_user_group
and ug.idn_user_group = #{shipper_code}
and exists (select 1 from tpa_tcontract_consolidate cc where cc.idn_contract = c.idn_contract) and (#{start_date} between c.start_date and c.end_date or #{end_date} between c.start_date and c.end_date) order by UPPER(value)

select n.idn_nomination, n.nomination_code, n.nomination_version, n.idn_operation, n.idn_user_group, ug.user_group_id, ug.short_name, n.idn_contract, c.contract_code, n.is_contracted, n.start_date, n.end_date, n.is_renomination, n.is_valid, n.is_responsed, n.feasibility_date, n.operator_comments, n.is_matched, n.matching_date, n.submission_comments, n.idn_operator_file, (select file_name from tpa_toperation_file where n.idn_operator_file = idn_operation_file) as operator_file_name from tpa_tnomination n, tpa_tuser_group ug, tpa_tcontract c where n.idn_user_group = ug.idn_user_group and n.idn_operation = (select idn_operation from tpa_toperation o, tpa_toperation_category oc, tpa_toperation_term ot where oc.idn_operation_category = o.idn_operation_category and oc.category_code = #{category_code} and ot.idn_operation_term = o.idn_operation_term and ot.term_code = #{term_code} ) and n.idn_contract = c.idn_contract and c.idn_pipeline_system = #{systemId} and n.nomination_version = (select max(n2.nomination_version) from tpa_tnomination n2 where n.nomination_code = n2.nomination_code AND n2.is_intraday = 'N' ) AND n.is_intraday = 'N'
and ug.idn_user_group = #{shipper_code}
and n.idn_contract = #{contract_code}
and #{start_date} >= n.start_date and nvl(n.end_date,#{start_date}) >= #{start_date} order by ug.user_group_id, c.contract_code
