mapper.select:
SELECT rc.idn_intraday_renom_cab, rc.intraday_renom_code, rc.gas_day, rc.hour_from as hour, rc.idn_user_group, nvl(ug.user_group_id, 'All shippers') AS user_group_code, nvl(ug.user_group_name, 'All shippers') AS user_group_name, rc.status FROM tpa_tintraday_renom_cab rc LEFT JOIN tpa_tuser_group ug ON rc.idn_user_group = ug.idn_user_group WHERE rc.gas_day between TRUNC(sysdate) and TRUNC(sysdate + 1)
and ug.idn_user_group = #{shipperId}
and rc.status = #{status}
ORDER BY rc.intraday_renom_code

mapper.update:
UPDATE tpa_tintraday_renom_cab set
	status = #{status},
    aud_last_date=sysdate,
    aud_last_user=#{username}
WHERE idn_intraday_renom_cab = #{idn_intraday_renom_cab}
