mapper.select:
SELECT ug.user_group_id, c.contract_code, tc.idn_nom_template_contract, tc.document_name, tc.binary_data, o.operation_desc, tc.comments FROM TPA_TNOM_TEMPLATE_CONTRACT tc, TPA_TUSER_GROUP ug, TPA_TOPERATION o, TPA_TCONTRACT c WHERE tc.idn_user_group = ug.idn_user_group AND tc.idn_operation = o.idn_operation AND tc.idn_contract = c.idn_contract
AND tc.idn_user_group = #{shipperId}
AND tc.idn_contract = #{contractId}

select c.idn_contract as key, c.contract_code as value
from
TPA_TCONTRACT c
where c.idn_pipeline_system = #{systemId}

and c.idn_user_group = #{shipperId}

order by 2
