mapper.select:
select tfl.idn_offspec_status_flow
from tpa_toffspec_status_flow tfl
where tfl.flow_code=#{incidentTypeCode}

select tsp.point_code
from tpa_tsystem_point tsp
where tsp.idn_system_point=#{pointId}

select tgr.idn_user_group 
from tpa_tuser_group tgr
where tgr.user_group_id=#{groupCode}

select tsta.idn_offspec_status as statusId, tsta.STATUS_CODE as statusCode, tsta.status_desc as statusDesc, tsta.idn_offspec_status_flow as incidentTypeId, tflow.flow_desc as incidentTypeDesc, tsta.sort_value as statusSort from tpa_toffspec_status tsta, tpa_toffspec_status_flow tflow where tflow.idn_offspec_status_flow = tsta.idn_offspec_status_flow
and tsta.idn_offspec_status_flow = #{incidentTypeId}
order by tsta.sort_value

    select tsta.idn_offspec_status
		from tpa_toffspec_status tsta
    where tsta.status_code=#{statusCode}

with pipeline_points as (select distinct spp.idn_quality_point as idn_quality_point from tpa_vsystem_point sp, tpa_tsystem_point_param spp, tpa_varea_tpa a, tpa_vzone_tpa z where sp.idn_system_point = spp.idn_system_point and sp.group_code = 'METERED' and spp.idn_quality_point is not null and spp.version_date = (select max(sppx.version_date) from tpa_tsystem_point_param sppx where sppx.idn_system_point = spp.idn_system_point and sppx.start_date = spp.start_date) and sp.idn_area = a.idn_area and a.idn_zone = z.idn_zone and z.idn_pipeline_system = #{systemId}) select * from (select tospec.idn_offspec, tstatus.idn_offspec_status_flow, tflow.flow_desc, tospec.offspec_code, tlogmax.idn_offspec_log, tsp.idn_system_point, tsp.point_code, tospec.idn_user_group, tgr.user_group_id, tospec.start_date, tospec.end_date, tospec.aud_last_date, tstatus.idn_offspec_status, ac.action_desc, tstatus.status_desc, tlogmin.user_comments as initial_comments, tlogmin.idn_user as idn_first_user, (select u.user_id from tpa_tuser u where u.idn_user = tlogmin.idn_user) as first_user, tospec.idn_offspec_action as idnAction, (select a.action_desc from tpa_toffspec_action a where a.idn_offspec_action = tospec.idn_offspec_action) as action from tpa_toffspec tospec, tpa_tuser_group tgr, tpa_tsystem_point tsp, tpa_toffspec_status tstatus, tpa_toffspec_status_flow tflow, tpa_toffspec_log tlogmax, tpa_toffspec_log tlogmin, tpa_toffspec_action ac, pipeline_points pp where tgr.idn_user_group (+)= tospec.idn_user_group
and tospec.offspec_code like #{incidentCode}
and tospec.idn_system_point=#{qualityPointId}
and nvl(tospec.idn_user_group,tlogmax.idn_user_group)=#{shipperId}
#{item}
and tospec.idn_offspec_status in (-1)
and nvl(tospec.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))>= #{startDate}
and #{endDate} >= tospec.start_date
and tsp.idn_system_point = tospec.idn_system_point and tsp.idn_system_point = pp.idn_quality_point and tstatus.idn_offspec_status = tospec.idn_offspec_status and tflow.idn_offspec_status_flow = tstatus.idn_offspec_status_flow and tlogmax.idn_offspec = tospec.idn_offspec and tlogmax.insert_date = (select max(tlog3.insert_date) from tpa_toffspec_log tlog3 where tlog3.idn_offspec = tlogmax.idn_offspec) and tlogmin.idn_offspec = tospec.idn_offspec and tlogmin.insert_date = (select min(tlog4.insert_date) from tpa_toffspec_log tlog4 where tlog4.idn_offspec = tlogmin.idn_offspec) and ac.idn_offspec_action(+) = tospec.idn_offspec_action union select tospec.idn_offspec, tstatus.idn_offspec_status_flow, tflow.flow_desc, tospec.offspec_code, tlogmax.idn_offspec_log, tsp.idn_system_point, tsp.point_code, tospec.idn_user_group, tgr.user_group_id, tospec.start_date, tospec.end_date, tospec.aud_last_date, tstatus.idn_offspec_status, ac.action_desc, tstatus.status_desc, tlogmin.user_comments as initial_comments, tlogmin.idn_user as idn_first_user, (select u.user_id from tpa_tuser u where u.idn_user = tlogmin.idn_user) as first_user, tospec.idn_offspec_action as idnAction, (select a.action_desc from tpa_toffspec_action a where a.idn_offspec_action = tospec.idn_offspec_action) as action from tpa_toffspec tospec, tpa_tuser_group tgr, tpa_tsystem_point tsp, tpa_toffspec_status tstatus, tpa_toffspec_status_flow tflow, tpa_toffspec_log tlogmax, tpa_toffspec_log tlogmin, tpa_toffspec_response tores, tpa_toffspec_action ac, pipeline_points pp where tgr.idn_user_group (+)= tospec.idn_user_group
and tospec.offspec_code like #{incidentCode}
and tospec.idn_system_point=#{qualityPointId}
and tores.idn_user_group=#{shipperId}
and tores.idn_user_group=-1
#{item}
and tospec.idn_offspec_status in (-1)
and nvl(tospec.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))>= #{startDate}
and #{endDate} >= tospec.start_date
and tsp.idn_system_point = tospec.idn_system_point and tsp.idn_system_point = pp.idn_quality_point and tstatus.idn_offspec_status = tospec.idn_offspec_status and tflow.idn_offspec_status_flow = tstatus.idn_offspec_status_flow and tlogmax.idn_offspec = tospec.idn_offspec and tlogmax.insert_date = (select max(tlog3.insert_date) from tpa_toffspec_log tlog3 where tlog3.idn_offspec = tlogmax.idn_offspec) and tlogmin.idn_offspec = tospec.idn_offspec and tlogmin.insert_date = (select min(tlog4.insert_date) from tpa_toffspec_log tlog4 where tlog4.idn_offspec = tlogmin.idn_offspec) and tores.idn_offspec=tospec.idn_offspec and tores.idn_offspec_response = (select max(tres2.idn_offspec_response) from tpa_toffspec_response tres2 where tres2.idn_offspec=tores.idn_offspec and tres2.idn_user_group=tores.idn_user_group) and ac.idn_offspec_action(+) = tospec.idn_offspec_action) t order by t.point_code asc, t.user_group_id asc, t.start_date desc

with pipeline_points as (select distinct spp.idn_quality_point as idn_quality_point from tpa_vsystem_point sp, tpa_tsystem_point_param spp, tpa_varea_tpa a, tpa_vzone_tpa z where sp.idn_system_point = spp.idn_system_point and sp.group_code = 'METERED' and spp.idn_quality_point is not null and spp.version_date = (select max(sppx.version_date) from tpa_tsystem_point_param sppx where sppx.idn_system_point = spp.idn_system_point and sppx.start_date = spp.start_date) and sp.idn_area = a.idn_area and a.idn_zone = z.idn_zone and z.idn_pipeline_system = #{systemId}) select tospec.idn_offspec, tstatus.idn_offspec_status_flow, tflow.flow_desc, tospec.offspec_code, tlogmax.idn_offspec_log, tsp.idn_system_point, tsp.point_code, tospec.idn_user_group, tgr.user_group_id, tospec.start_date, tospec.end_date, tospec.aud_last_date, tstatus.idn_offspec_status, tstatus.status_desc, tores.file_name as file_name_resp, tores.attached_file, tstatus.file_on_response, (select tlogmin.user_comments from tpa_toffspec_log tlogmin where tlogmin.idn_offspec = tospec.idn_offspec and tlogmin.idn_offspec_log = (select min(tlog2.idn_offspec_log) from tpa_toffspec_log tlog2 where tlog2.idn_offspec = tlogmin.idn_offspec)) as initial_comments, tlogmax.user_comments, tlogmax.file_name, tores.operator_comments from tpa_toffspec tospec, tpa_tuser_group tgr, tpa_tsystem_point tsp, tpa_toffspec_status tstatus, tpa_toffspec_status_flow tflow, tpa_toffspec_log tlogmax, tpa_toffspec_response tores, pipeline_points pp where tgr.idn_user_group (+)= tospec.idn_user_group
and tospec.offspec_code like #{incidentCode}
and tospec.idn_system_point=#{qualityPointId}
and tores.idn_user_group=#{shipperId}
and tores.idn_user_group=-1
#{item}
and tospec.idn_offspec_status in (-1)
and nvl(tospec.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))>= #{startDate}
and #{endDate} >= tospec.start_date
and tsp.idn_system_point = tospec.idn_system_point and tsp.idn_system_point = pp.idn_quality_point and tstatus.idn_offspec_status = tospec.idn_offspec_status and tflow.idn_offspec_status_flow = tstatus.idn_offspec_status_flow and tlogmax.idn_offspec = tospec.idn_offspec and tlogmax.idn_offspec_log = (select max(tlog3.idn_offspec_log) from tpa_toffspec_log tlog3 where tlog3.idn_offspec = tlogmax.idn_offspec) and tores.idn_offspec=tospec.idn_offspec and tores.idn_offspec_response = (select max(tres2.idn_offspec_response) from tpa_toffspec_response tres2 where tres2.idn_offspec=tores.idn_offspec and tres2.idn_user_group=tores.idn_user_group)
(#{item2} = 1 AND tores.is_responded = 'N') OR (#{item2} = 2 AND tores.is_responded = 'Y' AND tores.response_value = 'OK') OR (#{item2} = 3 AND tores.is_responded = 'Y' AND tores.response_value != 'OK')
order by tsp.point_code asc, tospec.start_date desc

select tres.idn_offspec_response, tres.idn_user_group, tgr.user_group_id, tres.user_comments, tres.is_responded, tres.response_date, tres.response_value, tres.file_name, tres.attached_file, tes.file_on_response, tres.operator_comments from tpa_toffspec_response tres, tpa_toffspec tof, tpa_toffspec_status tes, tpa_tuser_group tgr where tres.idn_offspec=#{incidentId} and tgr.idn_user_group=tres.idn_user_group and tof.idn_offspec = tres.idn_offspec and tof.idn_offspec_status = tes.idn_offspec_status
and tres.idn_user_group=#{shipperId}
and tres.idn_offspec_response = (select max(tres2.idn_offspec_response) from tpa_toffspec_response tres2 where tres2.idn_offspec=tres.idn_offspec and tres2.idn_user_group=tres.idn_user_group) order by 1 asc

select tlog.attached_file
from tpa_toffspec_log tlog
where tlog.idn_offspec_log=#{logId}

mapper.update:
		update tpa_toffspec set
             aud_last_user=#{username},

			idn_user_group = #{newOriginatorShipperId},


			end_date = #{newEndDate},

	       	idn_offspec_status = #{newStatusId}
	    where idn_offspec=#{incidentId}

mapper.insert:
	insert into tpa_toffspec_response (idn_offspec_response,
	                                   idn_offspec,
	                                   idn_user_group,
	                                   is_responded,
	                                   response_value,
	                                   response_date,
	                                   user_comments,
	                                   idn_response_user,
	                                    operator_comments,

	                                    file_name,


	                                    attached_file,

                                        aud_ins_user,
                                        aud_last_user)
	values (tpa_soffspec_response.nextval
			,#{incidentId}
			,#{groupId}
			,#{isResponded}
			,#{responseValue}
			,#{responseDate}
			,#{userComments}
	        ,#{userId}
	        ,#{operatorComments}

			,#{fileName}


			,#{attachedFile}

      ,#{username}
      ,#{username})

mapper.select:
select f.file_name as fileName, f.binary_data as binaryData 
	from TPA_TOFFSPEC_ACTION_FILE f
	where f.idn_offspec = #{incidentId}

mapper.update:
update tpa_toffspec o 
	set o.idn_offspec_action = #{idnAction}
	where o.idn_offspec = #{incidentId}
