mapper.select:
SELECT user_group_id 
FROM tpa_tuser_group 
where idn_user_group= #{idn_user_group}

		SELECT cha.idn_tariff_charge
  FROM tpa_ttariff_charge cha
 WHERE cha.idn_user_group = #{idn_user_group}
   AND cha.charge_date = #{shortDate}
   AND cha.idn_pipeline_system = #{idn_system}
   AND cha.version_date = (SELECT MAX(chax.version_date)
                             FROM tpa_ttariff_charge chax
                            WHERE chax.idn_user_group = #{idn_user_group}
                              AND chax.charge_date = #{shortDate}
                              AND chax.idn_pipeline_system = #{idn_system})

SELECT tuser.idn_user_group as key, tuser.user_group_id as value FROM tpa_tuser_group tuser WHERE tuser.idn_user_group_type = (SELECT idn_user_group_type FROM tpa_tuser_group_type WHERE type_code = 'SHIPPER') and trunc(sysdate) between tuser.start_date and nvl(tuser.end_date, to_date('31/12/9999', 'DD/MM/YYYY'))
and tuser.idn_user_group = #{idn_user_group}
order by UPPER(value)

	 		    select tcode.tariff_code tariff_code
    			FROM tpa_ttariff_code tcode
   			    where tcode.idn_tariff_code= #{idn_tariff_code}

SELECT ptype.type_code type_code
FROM tpa_tsystem_point_type ptype
WHERE idn_system_point_type =  #{idn_system_point_type}

SELECT v_data.GAS_DAY AS detailGasDay, v_data.ENERGY AS dailyBookedEntryCapacity, ROUND(v_data.ENERGY * v_data.FEE, 2) AS dailyCapacityCharge FROM (SELECT csource.GAS_DAY, SUM(csource.BILLABLE_ENERGY) ENERGY, cmonth.FEE FROM TPA_TTARIFF_CHARGE_SOURCE csource, TPA_TTARIFF_CHARGE_MONTH cmonth, (SELECT DISTINCT dcalc.IDN_TARIFF_SOURCE_DETAIL, cdetail.IDN_TARIFF_CODE_DETAIL FROM TPA_TTARIFF_CODE_DET_CALC dcalc, TPA_TTARIFF_CODE_DETAIL cdetail WHERE cdetail.IDN_TARIFF_CODE = #{idn_tariff_code} AND dcalc.IDN_TARIFF_CODE_DETAIL = cdetail.IDN_TARIFF_CODE_DETAIL) calc_detail WHERE csource.IDN_TARIFF_CHARGE = #{idn_tariff_charge}
AND csource.idn_contract = #{idn_contract}
AND csource.IDN_TARIFF_SOURCE_DETAIL = calc_detail.IDN_TARIFF_SOURCE_DETAIL AND cmonth.IDN_TARIFF_CHARGE = csource.IDN_TARIFF_CHARGE AND cmonth.IDN_TARIFF_CODE_DETAIL = calc_detail.IDN_TARIFF_CODE_DETAIL AND cmonth.IDN_CONTRACT = csource.IDN_CONTRACT GROUP BY csource.GAS_DAY, cmonth.FEE) v_data ORDER BY v_data.GAS_DAY

SELECT v_data.GAS_DAY AS detailGasDay, v_data.ENERGY AS dailyAllocatedExitValue, ROUND(v_data.ENERGY * v_data.FEE, 2) AS dailyCommodityCharge FROM (SELECT csource.GAS_DAY, SUM(csource.BILLABLE_ENERGY) ENERGY, cmonth.FEE FROM TPA_TTARIFF_CHARGE_SOURCE csource, TPA_TTARIFF_CHARGE_MONTH cmonth, (SELECT DISTINCT dcalc.IDN_TARIFF_SOURCE_DETAIL, cdetail.IDN_TARIFF_CODE_DETAIL FROM TPA_TTARIFF_CODE_DET_CALC dcalc, TPA_TTARIFF_CODE_DETAIL cdetail WHERE cdetail.IDN_TARIFF_CODE = #{idn_tariff_code}
AND cdetail.IDN_SYSTEM_POINT_TYPE = #{idn_system_point_type}
AND dcalc.IDN_TARIFF_CODE_DETAIL = cdetail.IDN_TARIFF_CODE_DETAIL) calc_detail WHERE csource.IDN_TARIFF_CHARGE = #{idn_tariff_charge}
AND csource.idn_contract = #{idn_contract}
AND csource.IDN_TARIFF_SOURCE_DETAIL = calc_detail.IDN_TARIFF_SOURCE_DETAIL AND cmonth.IDN_TARIFF_CHARGE = csource.IDN_TARIFF_CHARGE AND cmonth.IDN_TARIFF_CODE_DETAIL = calc_detail.IDN_TARIFF_CODE_DETAIL AND cmonth.IDN_CONTRACT = csource.IDN_CONTRACT GROUP BY csource.GAS_DAY, cmonth.FEE) v_data ORDER BY v_data.GAS_DAY

SELECT v_data.GAS_DAY AS detailGasDay, v_data.ENERGY AS dailyAllocatedEntryValue, ROUND(v_data.ENERGY * v_data.FEE, 2) AS dailyCommodityCharge FROM (SELECT csource.GAS_DAY, SUM(csource.BILLABLE_ENERGY) ENERGY, cmonth.FEE FROM TPA_TTARIFF_CHARGE_SOURCE csource, TPA_TTARIFF_CHARGE_MONTH cmonth, (SELECT DISTINCT dcalc.IDN_TARIFF_SOURCE_DETAIL, cdetail.IDN_TARIFF_CODE_DETAIL FROM TPA_TTARIFF_CODE_DET_CALC dcalc, TPA_TTARIFF_CODE_DETAIL cdetail WHERE cdetail.IDN_TARIFF_CODE = #{idn_tariff_code}
AND cdetail.IDN_SYSTEM_POINT_TYPE = #{idn_system_point_type}
AND dcalc.IDN_TARIFF_CODE_DETAIL = cdetail.IDN_TARIFF_CODE_DETAIL) calc_detail WHERE csource.IDN_TARIFF_CHARGE = #{idn_tariff_charge}
AND csource.idn_contract = #{idn_contract}
AND csource.IDN_TARIFF_SOURCE_DETAIL = calc_detail.IDN_TARIFF_SOURCE_DETAIL AND cmonth.IDN_TARIFF_CHARGE = csource.IDN_TARIFF_CHARGE AND cmonth.IDN_TARIFF_CODE_DETAIL = calc_detail.IDN_TARIFF_CODE_DETAIL AND cmonth.IDN_CONTRACT = csource.IDN_CONTRACT GROUP BY csource.GAS_DAY, cmonth.FEE) v_data ORDER BY v_data.GAS_DAY

SELECT v_data.GAS_DAY AS detailGasDay, v_data.ENERGY AS dailyEntryCapacityOU, v_data.original_ENERGY AS dailyOriginalEntryCapacityOU, v_data.ENERGY * v_data.REDUCTION AS dailyReducedEntryCapacityOU, ROUND(v_data.ENERGY * v_data.REDUCTION * v_data.FEE, 2) AS dailyEntryCapacityOUCharge FROM (SELECT csource.GAS_DAY, SUM(csource.BILLABLE_ENERGY) ENERGY, SUM(csource.ENERGY) original_ENERGY, cmonth.REDUCTION/100 REDUCTION, cmonth.FEE FROM TPA_TTARIFF_CHARGE tcharge, TPA_TTARIFF_CHARGE_SOURCE csource, TPA_TTARIFF_CHARGE_MONTH cmonth, (SELECT DISTINCT dcalc.IDN_TARIFF_SOURCE_DETAIL, cdetail.IDN_TARIFF_CODE_DETAIL FROM TPA_TTARIFF_CODE_DET_CALC dcalc, TPA_TTARIFF_CODE_DETAIL cdetail WHERE cdetail.IDN_TARIFF_CODE = #{idn_tariff_code} AND cdetail.IDN_SYSTEM_POINT_TYPE = #{idn_system_point_type} AND dcalc.IDN_TARIFF_CODE_DETAIL = cdetail.IDN_TARIFF_CODE_DETAIL AND cdetail.idn_tariff_code_detail IN (SELECT DISTINCT idn_tariff_code_detail FROM TABLE(pck_tariff_charge.fun_paramet_tariff_code_det(#{idn_tariff_charge}, #{idn_system})))) calc_detail WHERE csource.IDN_TARIFF_CHARGE = #{idn_tariff_charge} AND tcharge.idn_tariff_charge = csource.idn_tariff_charge
AND csource.idn_contract = #{idn_contract}
AND csource.IDN_TARIFF_SOURCE_DETAIL = calc_detail.IDN_TARIFF_SOURCE_DETAIL AND cmonth.IDN_TARIFF_CHARGE = csource.IDN_TARIFF_CHARGE AND cmonth.IDN_TARIFF_CODE_DETAIL = calc_detail.IDN_TARIFF_CODE_DETAIL AND cmonth.IDN_CONTRACT = csource.IDN_CONTRACT GROUP BY csource.GAS_DAY, cmonth.REDUCTION, cmonth.FEE) v_data ORDER BY v_data.GAS_DAY

SELECT v_data.GAS_DAY AS detailGasDay, v_data.ENERGY AS dailyExitCapacityOU, v_data.original_ENERGY AS dailyOriginalExitCapacityOU, v_data.ENERGY * v_data.REDUCTION AS dailyReducedExitCapacityOU, ROUND(v_data.ENERGY * v_data.REDUCTION * v_data.FEE, 2) AS dailyExitCapacityOUCharge FROM (SELECT csource.GAS_DAY, SUM(csource.BILLABLE_ENERGY) ENERGY, SUM(csource.ENERGY) original_ENERGY, cmonth.REDUCTION/100 REDUCTION, cmonth.FEE FROM TPA_TTARIFF_CHARGE_SOURCE csource, TPA_TTARIFF_CHARGE_MONTH cmonth, (SELECT DISTINCT dcalc.IDN_TARIFF_SOURCE_DETAIL, cdetail.IDN_TARIFF_CODE_DETAIL FROM TPA_TTARIFF_CODE_DET_CALC dcalc, TPA_TTARIFF_CODE_DETAIL cdetail WHERE cdetail.IDN_TARIFF_CODE = #{idn_tariff_code} AND cdetail.IDN_SYSTEM_POINT_TYPE = #{idn_system_point_type} AND dcalc.IDN_TARIFF_CODE_DETAIL = cdetail.IDN_TARIFF_CODE_DETAIL AND cdetail.idn_tariff_code_detail IN (SELECT DISTINCT idn_tariff_code_detail FROM TABLE(pck_tariff_charge.fun_paramet_tariff_code_det(#{idn_tariff_charge}, #{idn_system})))) calc_detail WHERE csource.IDN_TARIFF_CHARGE = #{idn_tariff_charge} AND tcharge.idn_tariff_charge = csource.idn_tariff_charge
AND csource.idn_contract = #{idn_contract}
AND csource.IDN_TARIFF_SOURCE_DETAIL = calc_detail.IDN_TARIFF_SOURCE_DETAIL AND cmonth.IDN_TARIFF_CHARGE = csource.IDN_TARIFF_CHARGE AND cmonth.IDN_TARIFF_CODE_DETAIL = calc_detail.IDN_TARIFF_CODE_DETAIL AND cmonth.IDN_CONTRACT = csource.IDN_CONTRACT GROUP BY csource.GAS_DAY, cmonth.REDUCTION, cmonth.FEE) v_data ORDER BY v_data.GAS_DAY
